import cchardetfrom powerspider import loggerfrom requests import request, RequestExceptiondef downloader(url, method=None, header=None, timeout=None, binary=False, **kwargs):    logger.info(f'Scraping {url}')    _headers = {        'User-Agent': ('Mozilla/5.0 (compatible; MSIE 9.0; '                       'Windows NT 6.1; Win64; x64; Trident/5.0)'),    }    _maxTimeout = timeout if timeout else 5    _headers = header if header else _headers    _method = "GET" if not method else method    try:        response = request(method=_method, url=url, headers=header, **kwargs)        encoding = cchardet.detect(response.content)['encoding']        if response.status_code == 200:            return response.content if binary else response.content.decode(encoding)        elif 200 < response.status_code < 400:            logger.info(f"Redirect_URL: {response.url}")        logger.error('Get invalid status code %s while scraping %s', response.status_code, url)    except RequestException as e:        logger.error(f'Error occurred while scraping {url}, Msg: {e}', exc_info=True)if __name__ == '__main__':    print(downloader("https://www.baidu.com/", "GET"))